sys_variable_value.value:
(function execute(inputs, outputs) {
//Get all the properties and process to send to Flow
//Pull all the properties as variables, this way if adjustments need to be made in the future it can be easily done here

//string
var review_period_name = gs.getProperty('x_398999_perform_0.review_period_name');

//string
var filter_criteria = gs.getProperty('x_398999_perform_0.review_user_filter_criteria');

// Date/Time object
var notification_reminder = new GlideDateTime(gs.getProperty('x_398999_perform_0.notify_reminder')).getDate();

// Date/Time object
var deadline_date = new GlideDateTime(gs.getProperty('x_398999_perform_0.review_confirmation_deadline')).getDate();

 // Date/Time object
var end_date = new GlideDateTime(gs.getProperty('x_398999_perform_0.end_date')).getDate();

   // Date/Time object
var start_date = new GlideDateTime(gs.getProperty('x_398999_perform_0.review_period_start_date')).getDate();

    //build the inputs needed to launch the flow
    outputs.review_period_name = review_period_name; // String 
    outputs.filter_criteria = filter_criteria; // String 
    outputs.notification_reminder = notification_reminder; // Date 
    outputs.deadline_date = deadline_date; // Date
    outputs.end_date = end_date; //Date
    outputs.start_date = start_date; //Date

})(inputs, outputs);

