sys_variable_value.value:
(function execute(inputs, outputs) {
/**** Update System Property Dates for next run *************/

  try{
    var success = true;
//pull the number of months until the next run, and make sure it is an integer
var process_schedule = parseInt(gs.getProperty('x_398999_perform_0.review_process_schedule'));

//pull the start date from system properties
//convert it to a Date/Time object
var start = new GlideDateTime(gs.getProperty('x_398999_perform_0.review_period_start_date'));

//add the number of months until the next review period
start.addMonthsUTC(gs.getProperty('x_398999_perform_0.review_process_schedule'));

//get just the date (drop the time)
var startDate = start.getDate();
gs.setProperty('x_398999_perform_0.review_period_start_date', startDate);

//pull the prompt date from system properties
//convert it to a Date/Time object
var prompt = new GlideDateTime(gs.getProperty('x_398999_perform_0.employee_prompt_self_review'));

//add the number of months until the next review period
prompt.addMonthsUTC(process_schedule);

//get just the date (drop the time)
var promptDate = prompt.getDate();
gs.setProperty('x_398999_perform_0.employee_prompt_self_review', promptDate);

// Set the Review Period Name property
var reviewPeriod = new x_398999_perform_0.PRUtils().setReviewPeriod(process_schedule,promptDate);
gs.setProperty('x_398999_perform_0.review_period_name', reviewPeriod);

//pull the notify date from system properties
//convert it to a Date/Time object
var notify = new GlideDateTime(gs.getProperty('x_398999_perform_0.notify_reminder'));

//add the number of months until the next review period
notify.addMonthsUTC(process_schedule);

//get just the date (drop the time)
var notifyDate = notify.getDate();
gs.setProperty('x_398999_perform_0.notify_reminder', notifyDate);

//pull the deadline date from system properties
//convert it to a Date/Time object
var deadline = new GlideDateTime(gs.getProperty('x_398999_perform_0.review_confirmation_deadline'));

//add the number of months until the next review period
deadline.addMonthsUTC(process_schedule);

//get just the date (drop the time)
var deadlineDate = deadline.getDate();
gs.setProperty('x_398999_perform_0.review_confirmation_deadline', deadlineDate);

 //pull the deadline date from system properties
//convert it to a Date/Time object
var end = new GlideDateTime(gs.getProperty('x_398999_perform_0.end_date'));

//add the number of months until the next review period
end.addMonthsUTC(process_schedule);

//get just the date (drop the time)
var endDate = end.getDate();
gs.setProperty('x_398999_perform_0.end_date', endDate);

// Sync the settings record with the properties

var settings_gr = new GlideRecord('x_398999_perform_0_performance_review')
if(settings_gr.get(gs.getProperty('x_398999_perform_0.settings_record'))){
    settings_gr.setWorkflow(false);
    settings_gr.setValue('review_period_start_date', gs.getProperty("x_398999_perform_0.review_period_start_date"));
    settings_gr.setValue('end_date', gs.getProperty("x_398999_perform_0.end_date"));
    settings_gr.setValue('employee_prompt_self_review', gs.getProperty("x_398999_perform_0.employee_prompt_self_review"));
    settings_gr.setValue('notify_reminder', gs.getProperty("x_398999_perform_0.notify_reminder"));
    settings_gr.setValue('evaluation_due', gs.getProperty("x_398999_perform_0.review_confirmation_deadline"));
    settings_gr.update();
}

} catch(err){
  outputs.success=false;
  outputs.error = err;
}
  })(inputs, outputs);

