sys_script_include.script:
var PRSettings = Class.create();
PRSettings.prototype = {

    createRecord: function() {
        try {
            //create a new settings record on the performance review table
            var settings_gr = new GlideRecord('x_398999_perform_0_performance_review');
            settings_gr.initialize();
            settings_gr.setWorkflow(false);
            settings_gr.setValue('active', false);
            settings_gr.setValue('review_period', 'Settings');
            settings_gr.setValue('number', 'EPR0000000');
            settings_gr.setValue('review_period_start_date', gs.getProperty("x_398999_perform_0.review_period_start_date"));
            settings_gr.setValue('end_date', gs.getProperty("x_398999_perform_0.end_date"));
            settings_gr.setValue('employee_prompt_self_review', gs.getProperty("x_398999_perform_0.employee_prompt_self_review"));
            settings_gr.setValue('notify_reminder', gs.getProperty("x_398999_perform_0.notify_reminder"));
            settings_gr.setValue('evaluation_due', gs.getProperty("x_398999_perform_0.review_confirmation_deadline"));
            settings_gr.setValue('notify_if_manager_isn_t_specified', gs.getProperty("x_398999_perform_0.mgr_empty_notify"));
            settings_gr.setValue('title_empty_notify', gs.getProperty("x_398999_perform_0.title_empty_notify"));
            settings_gr.setValue('review_process_schedule', gs.getProperty("x_398999_perform_0.review_process_schedule"));

            //if the filter value is not empty, remove the encoded query and set just the sys ids
            var filter = gs.getProperty("x_398999_perform_0.review_user_filter_criteria");
            if (!gs.nil(filter)) {
                filter = filter.replace("sys_idIN", '');
                settings_gr.setValue('emp_filter', filter);
            }

            //capture the sys id of the new record
            var settings_sys_id = settings_gr.insert();


            //if the sys_id of the new reocrd isn't empty
            if (!gs.nil(settings_sys_id)) {

                //set the sys_id to the property value
                gs.setProperty('x_398999_perform_0.settings_record', settings_sys_id);
				return settings_sys_id;
            }
        } catch (err) {
            gs.error("SI:{0} has encountered the following error: {1}", this.type + '', err);
        }
    },

    type: 'PRSettings'
};
