sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    var _status;

    // API call to get the actual state value of the record , 
    // pass the actual value instead of display value
    const {
        snHttp
    } = helpers;

    //api/now/table/x_398999_perform_0_performance_review?sysparm_query=sys_id%3D08fbd46f45212010f877828265535a27&sysparm_display_value=false&sysparm_fields=state
    snHttp('/api/now/table/x_398999_perform_0_performance_review?sysparm_display_value=false&sysparm_fields=state&sysparm_query=sys_id=' + event.payload.sysId, {
        method: 'GET',
        batch: false,
    }).then(response => {
        if (response.response.result) {

           // console.log('Response state here' + (response.response.result[0].state));
            _status = response.response.result[0].state;
            helpers.navigate.to('emp-evaluation', {
                sysId: event.payload.sysId,
                status: _status
            });
        }

    }).catch(error => {

        console.log('error in api');
        helpers.navigate.to('home');
    });

/*
    switch (event.payload.status) {
        case "Update Requested":
            _status = "Update-Requested";
            break;
        case "Manager Review":
            _status = "Manager-Review";
            break;
        default:
            _status = event.payload.status

    }

*/


}
