sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {


    var frequency = api.state.schedule_new_val;

    var revType = '';
    switch (frequency) {
        case '12':
            revType = 'Annual';
            break;
        case '6':
            revType = 'Bi-Annual';
            break;
        case '0':
            revType = 'Manual';
            break;
        //case 'custom':
        default:
            revType = "Custom";
            break;
    }
  api.setState('reviewSchedule', "Review schedule: " + revType);
//helpers.translate("Review schedule: {0}" , revType).then ((value) => { api.setState('reviewSchedule',value); });
}
