sys_ux_client_script.required_translations:
[ {
  "code" : null,
  "message" : "Record is successfully deleted.",
  "comment" : ""
}, {
  "code" : null,
  "message" : "Changed are save successfully",
  "comment" : ""
} ]

sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    debugger;
    // const {
    //     status
    // } = event.payload;

    //if (status == "updated") {
    if (api.state.pr_screen_status == "updated")
        helpers.translate('Changed are save successfully').then((value) => {
            api.emit("NOW_UXF_PAGE#ADD_NOTIFICATIONS", {
                items: [{
                    id: 'save_changes',
                    status: "positive",
                    icon: "check-outline",
                    header: "Changes saved",
                    content: {
                        type: "html",
                        value: "<span><br>" + value + "</span>"
                    },
                    action: {
                        type: "dismiss"
                    }
                }]
            });
        });
}
// this is not showing as the record is deleted and is routed to the home page.
if (status == "closed") {
    helpers.translate('Record is successfully deleted.').then((value) => {
        api.emit("NOW_UXF_PAGE#ADD_NOTIFICATIONS", {
            items: [{
                id: 'save_changes',
                status: "positive",
                icon: "bell-outline",
                header: "Record deleted",
                content: value,
                action: {
                    type: "dismiss"
                }
            }]
        });
    });
}

}
