sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {

    api.data.gform.setValue(
        {
            fieldName:'manager_feedback',
            value:api.state.mgr_feedback
        }
    );

    api.data.gform.setValue(
        {
            fieldName:'state',
            value:'update_requested'
        }
    );

    api.data.gform.save();

    //close the custom modal window
    helpers.modal.close('custom_1');
}
