sys_ux_client_script.required_translations:
[ {
  "code" : null,
  "message" : "You have sent your self evaluation to your manager for review.",
  "comment" : ""
} ]

sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {
    const header = await helpers.translate('You have sent your self evaluation to your manager for review.');

    api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
        items: [{
            id: 'draft-notify',
            status: 'positive',
            icon: 'check-outline',
            header,
            content,
            action: {
                type: 'dismiss'
            }
        }]
    });
}
