sys_ux_client_script.required_translations:
[ {
  "code" : null,
  "message" : "Your self evaluation has been saved as draft.",
  "comment" : ""
} ]

sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
async function handler({
    api,
    event,
    helpers,
    imports
}) {
    //clear all notifications
    api.emit('NOW_UXF_PAGE#CLEAR_NOTIFICATIONS');

    const content = await helpers.translate('Your self evaluation has been saved as draft.');

    api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
        items: [{
            id: 'draft-notify',
            status: 'info',
            icon: 'check-outline',
            content,
            action: {
                type: 'dismiss'
            }
        }]
    });
}
