sys_ux_data_broker_graphql.script:
(function buildQuery(query, inputs) {
    var userSysId = (inputs.userSysId || "").trim();

    if (gs.nil(userSysId))
        userSysId = gs.getUserID();

    var userQuery =
        "{\r\n" +
        "  GlideRecord_Query {\r\n" +
        "    sys_user(sys_id: \"" + userSysId + "\") {\r\n" +
		"       _results {\r\n" + 
		"        company {\r\n" +
		"            _reference {\r\n" +
		"                name { value }\r\n" +
        "                banner_image { value }\r\n" +
        "            }\r\n" +
		"         }\r\n" + 
		"       }\r\n" + 
        "    }\r\n" +
        "  }\r\n" +
        "}";

    query.append(userQuery);
})(query, inputs);
